    public function testCreateWithDataTranslations() {
       
        $tableData = array(
            array('name', 'datetime', 'venue', 'description'),
            array('PHPNW', '08/10/2011 09:00', 'Ramada Hotel', 'A Great Conf!')
        );

        $tableNode = m::mock('Behat\Gherkin\Node\TableNode');
        $tableNode->shouldReceive('getRows')
                ->withNoArgs()
                ->andReturn($tableData)
                ->once();

        $expectedInsert = array('name' => 'PHPNW',
            'datetime' => '2011-10-08 09:00:00',
            'venue' => 'Ramada Hotel',
            'description' => 'A Great Conf!');

        $this->mockedConnection->shouldReceive('insert')
                ->with('Event', $expectedInsert)
                ->once();

        $this->object->setTableName('Event');

        $retFn = function($date) {
                    $date = \DateTime::createFromFormat('d/m/Y H:i', $date);
                    return $date->format('Y-m-d H:i:s');
                };

        $this->mockedBus->shouldReceive('getDataTranslation')
                ->with('UKTOMYSQLDATE')
                ->once()
                ->andReturn($retFn);


        $this->object->setDataTranslations(array('datetime' => 'UKTOMYSQLDATE'));

        $this->object->createFromTable($tableNode);
    }

    public function testCreateWithMultipleFeaturesEnabled() {
        $tableData = array(
            array('Name', 'Date', 'Venue'),
            array('PHPNW', '08/10/2011 09:00', 'Ramada Hotel')
        );

        $tableNode = m::mock('Behat\Gherkin\Node\TableNode');
        $tableNode->shouldReceive('getRows')
                ->withNoArgs()
                ->andReturn($tableData)
                ->once();

        $expectedInsert = array('name' => 'PHPNW',
            'datetime' => '2011-10-08 09:00:00',
            'venue' => 'Ramada Hotel',
            'description' => 'TEST DESCRIPTION');

        $this->mockedConnection->shouldReceive('insert')
                ->with('Event', $expectedInsert)
                ->once();

        $this->object->setTableName('Event');
        $this->object->setNameTranslations(array('Date' => 'datetime'));

        $this->object->setDefaults(array(
            'description' => 'TEST DESCRIPTION',
            'venue' => 'TEST VENUE'
        ));

        $retFn = function($date) {
                    $date = \DateTime::createFromFormat('d/m/Y H:i', $date);
                    return $date->format('Y-m-d H:i:s');
                };

        $this->mockedBus->shouldReceive('getDataTranslation')
                ->with('UKTOMYSQLDATE')
                ->once()
                ->andReturn($retFn);

        $this->object->setDataTranslations(array('datetime' => 'UKTOMYSQLDATE'));

        $this->object->createFromTable($tableNode);
    }

    public function testGetNamedItemId() {
        $tableData = array(
            array('name', 'datetime', 'venue', 'description'),
            array('PHPNW', '2011-10-08 09:00:00', 'Ramada Hotel', 'A Great Conf!')
        );

        $tableNode = m::mock('Behat\Gherkin\Node\TableNode');
        $tableNode->shouldReceive('getRows')
                ->withNoArgs()
                ->andReturn($tableData)
                ->once();

        $expectedInsert = array('name' => 'PHPNW',
            'datetime' => '2011-10-08 09:00:00',
            'venue' => 'Ramada Hotel',
            'description' => 'A Great Conf!');

        $this->mockedConnection->shouldReceive('insert')
                ->with('Event', $expectedInsert)
                ->once();

        $this->object->setTableName('Event');

        $this->object->createFromTable($tableNode);

        $this->assertInternalType('integer', $this->object->getNamedItemId('PHPNW'));
        $this->assertEquals(12, $this->object->getNamedItemId('PHPNW'));
    }

